import React, { useState } from 'react';
import EmployeeCard from './components/EmployeeCard';
import TaskCard from './components/TaskCard';
import SelectedItemsPanel from './components/SelectedItemsPanel';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  // Стан для відстеження вибраних елементів
  const [selectedEmployees, setSelectedEmployees] = useState([]);
  const [selectedTasks, setSelectedTasks] = useState([]);

  // Дані співробітників з зображеннями
  const employees = [
    { 
      id: 1, 
      name: "Іван Петренко", 
      position: "Менеджер", 
      department: "Відділ продажів", 
      email: "ivan@example.com", 
      phone: "+380991234567",
      imgurl: "/imgs/employees/employee1.jpg" 
    },
    // ...інші співробітники
  ];

  // Дані завдань з зображеннями
  const tasks = [
    { 
      id: 1, 
      title: "Завершити проект", 
      description: "Закінчити розробку нового модуля", 
      assignee: "Іван Петренко", 
      dueDate: "2023-12-31", 
      status: "В процесі",
      imgurl: "/imgs/tasks/task1.jpg" 
    },
    // ...інші завдання
  ];

  // Обробник вибору співробітника
  const handleEmployeeSelect = (employee) => {
    setSelectedEmployees(prev => 
      prev.some(e => e.id === employee.id) 
        ? prev.filter(e => e.id !== employee.id) 
        : [...prev, employee]
    );
  };

  // Обробник вибору завдання
  const handleTaskSelect = (task) => {
    setSelectedTasks(prev => 
      prev.some(t => t.id === task.id) 
        ? prev.filter(t => t.id !== task.id) 
        : [...prev, task]
    );
  };

  return (
    <div className="App container">
      <h1 className="my-4">Система управління підприємством</h1>

      {/* Панель вибраних елементів */}
      <SelectedItemsPanel 
        employees={selectedEmployees} 
        tasks={selectedTasks} 
      />

      {/* Секція співробітників */}
      <section className="mb-5">
        <h2>Співробітники</h2>
        <div className="row">
          {employees.map(employee => (
            <EmployeeCard 
              key={employee.id}
              employee={employee}
              onSelect={() => handleEmployeeSelect(employee)}
              isSelected={selectedEmployees.some(e => e.id === employee.id)}
            />
          ))}
        </div>
      </section>

      {/* Секція завдань */}
      <section>
        <h2>Завдання</h2>
        <div className="row">
          {tasks.map(task => (
            <TaskCard 
              key={task.id}
              task={task}
              onSelect={() => handleTaskSelect(task)}
              isSelected={selectedTasks.some(t => t.id === task.id)}
            />
          ))}
        </div>
      </section>
    </div>
  );
}

export default App;
