// src/components/GoodsComponent.js
import React, { useEffect } from 'react';
import { LOCALSTORE_TOTALITEMS } from '../constants';

const GoodsComponent = ({ itemsList, totalItems, setTotalItems }) => {
  // Додавання товару до кошика
  const addItem = (item) => {
    const newItems = [...totalItems, item];
    setTotalItems(newItems);
    localStorage.setItem(LOCALSTORE_TOTALITEMS, JSON.stringify(newItems));
  };

  // Слухач змін у localStorage (для синхронізації між вкладками)
  useEffect(() => {
    const handleStorageChange = () => {
      const savedItems = localStorage.getItem(LOCALSTORE_TOTALITEMS);
      if (savedItems) setTotalItems(JSON.parse(savedItems));
    };
    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, [setTotalItems]);

  return (
    <div className="goods-container">
      <h2>Список товарів</h2>
      {itemsList.map((item) => (
        <div key={item.id} className="goods-item">
          <span>{item.name}</span>
          <button onClick={() => addItem(item)}>Додати до кошика</button>
        </div>
      ))}
    </div>
  );
};

export default GoodsComponent;
