import React, { useState } from 'react';
import { LOCALSTORE_TOTALITEMS } from './constants';
import GoodsComponent from './components/GoodsComponent';
import BasketComponent from './components/BasketComponent';
import './App.css';

const itemsList = [
  { id: 1, name: "Яблука" },
  { id: 2, name: "Банани" },
  { id: 3, name: "Апельсини" },
];

function App() {
  const [totalItems, setTotalItems] = useState(() => {
    const savedItems = localStorage.getItem(LOCALSTORE_TOTALITEMS);
    return savedItems ? JSON.parse(savedItems) : [];
  });

  return (
    <div className="app">
      <GoodsComponent 
        itemsList={itemsList} 
        totalItems={totalItems} 
        setTotalItems={setTotalItems}
      />
      <BasketComponent 
        totalItems={totalItems} 
        setTotalItems={setTotalItems}
      />
    </div>
  );
}

export default App;
