import React, { useState } from 'react';
import './App.css';
import MenuComponent from "./MenuComponent.js";
import CardItemComponent from "./CardItemComponent.js";

function App() {
  const [selectedItems, setSelectedItems] = useState([]);

  const itemList = [
    { id: 1, name: "Apple granny smith organic", imgurl: "https://m.media-amazon.com/images/I/71MzPgrIaoL.jpg", price: 20 },
    { id: 2, name: "Apple pink lady organic", imgurl: "https://ipcdn.freshop.com/resize?url=https://images.freshop.com/2522137/19cf994ff15b19b6d0429893150c29c7_large.png&width=512&type=webp&quality=90", price: 22 },
    { id: 3, name: "Baby capsicums organic mixed colour", imgurl: "https://alnamoosfresh.ae/public/uploads/all/eVuIELQ5YlPZ9Byjv2yIuTWsFlooJTys1zbJsTdf.jpg", price: 30 },
    { id: 4, name: "Banana organic", imgurl: "https://produits.bienmanger.com/36986-0w470h470_Organic_Banana_From_The_West_Indies.jpg", price: 35 },
    { id: 5, name: "Blueberries organic", imgurl: "https://tusupertogo.com/wp-content/uploads/2020/05/22203.jpg", price: 155 },
    { id: 6, name: "Apple granny smith organic each", imgurl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ0zcwtqRKH3FTcF9PFxq3kVOuM7peRpAhBhw&s", price: 20 },
  ];

  const handleItemSelect = (item) => {
    setSelectedItems([...selectedItems, item]);
  };

  return (
    <>
      <MenuComponent selectedCount={selectedItems.length} />
      <section className="mt-3 container">
        <div className="row">
          {itemList.map((item) => (
            <CardItemComponent
              key={item.id}
              card={item}
              onSelect={handleItemSelect}
              isSelected={selectedItems.includes(item)}
            />
          ))}
        </div>
      </section>
    </>
  );
}

export default App;
