  const handleEmployeeSelect = (employee) => {
    setSelectedEmployees(prev => 
      prev.some(e => e.id === employee.id) 
        ? prev.filter(e => e.id !== employee.id) 
        : [...prev, employee]
    );
  };

  const handleTaskSelect = (task) => {
    setSelectedTasks(prev => 
      prev.some(t => t.id === task.id) 
        ? prev.filter(t => t.id !== task.id) 
        : [...prev, task]
    );
  };

  const handleAssignTasks = () => {
    if (selectedEmployees.length === 0 || selectedTasks.length === 0 || !assignmentName.trim()) return;

    const newAssignment = {
      id: Date.now(),
      name: assignmentName,
      employees: selectedEmployees.map(e => ({ 
        id: e.id, 
        name: e.name,
        position: e.position
      })),
      tasks: selectedTasks.map(t => ({
        id: t.id,
        title: t.title,
        status: t.status
      })),
      createdAt: new Date().toISOString()
    };

    // Оновлюємо стан та localStorage
    const updatedAssignments = [...assignments, newAssignment];
    setAssignments(updatedAssignments);
    localStorage.setItem('assignments', JSON.stringify(updatedAssignments));

    // Показуємо сповіщення
    setAssignmentDetails({
      name: assignmentName,
      employees: selectedEmployees.map(e => e.name),
      tasks: selectedTasks.map(t => t.title),
      timestamp: new Date().toLocaleString()
    });
    setShowSuccess(true);

    // Очищаємо вибір
    setSelectedEmployees([]);
    setSelectedTasks([]);
    setAssignmentName('');

    // Автоматично ховаємо сповіщення через 5 секунд
    setTimeout(() => setShowSuccess(false), 5000);
  };

  return (
