                <p><strong>Співробітники:</strong> {assignmentDetails.employees.join(', ')}</p>
                <p><strong>Завдання:</strong> {assignmentDetails.tasks.join(', ')}</p>
                <p><small>{assignmentDetails.timestamp}</small></p>
              </div>
            )}
          </Toast.Body>
        </Toast>
      </ToastContainer>

      {/* Кнопка призначення завдань з полем для назви */}
      {selectedEmployees.length > 0 && selectedTasks.length > 0 && (
        <div className="card mb-4 p-3">
          <div className="mb-3">
            <label htmlFor="assignmentName" className="form-label">
              Назва призначення:
            </label>
            <input
              type="text"
              className="form-control"
              id="assignmentName"
              value={assignmentName}
              onChange={(e) => setAssignmentName(e.target.value)}
              placeholder="Введіть назву призначення"
            />
          </div>
          <div className="text-center">
            <button 
              className="btn btn-success btn-lg"
              onClick={handleAssignTasks}
              disabled={!assignmentName.trim()}
            >
              <i className="bi bi-check-circle me-2"></i>
              Створити призначення
            </button>
          </div>
        </div>
      )}

      <SelectedItemsPanel 
        employees={selectedEmployees} 
        tasks={selectedTasks} 
      />

      {/* Секція призначень */}
      <AssignmentsList assignments={assignments} />

      {/* Секція внутрішніх задач */}
      <TodoSection 
        tasks={todoTasks}
        onAdd={addTask}
        onDelete={deleteTask}
        onToggle={toggleTask}
      />

      {/* Секція співробітників */}
      <section className="mb-5">
        <h2>Співробітники</h2>
        <div className="row">
          {employees.map(employee => (
            <EmployeeCard 
              key={employee.id}
              employee={employee}
              onSelect={() => handleEmployeeSelect(employee)}
              isSelected={selectedEmployees.some(e => e.id === employee.id)}
            />
          ))}
        </div>
      </section>

      {/* Секція завдань */}
      <section>
        <h2>Завдання</h2>
        <div className="row">
          {tasks.map(task => (
            <TaskCard 
              key={task.id}
              task={task}
              onSelect={() => handleTaskSelect(task)}
              isSelected={selectedTasks.some(t => t.id === task.id)}
            />
          ))}
        </div>
      </section>
    </div>
  );
