import React, { useState, useEffect } from 'react';
import { LOCALSTORE_TOTALITEMS } from '../constants';

const BasketComponent = ({ totalItems, setTotalItems }) => { // Додано пропси
  // Функція видалення товару
  const removeItem = (itemId) => {
    const updatedItems = totalItems.filter((item) => item.id !== itemId);
    setTotalItems(updatedItems);
    localStorage.setItem(LOCALSTORE_TOTALITEMS, JSON.stringify(updatedItems));
  };

  return (
    <div className="basket-container">
      <h2>Кошик</h2>
      {totalItems.length === 0 ? (
        <p>Кошик порожній</p>
      ) : (
        totalItems.map((item) => (
          <div key={item.id} className="basket-item">
            {item.name}
            <button 
              onClick={() => removeItem(item.id)} 
              className="remove-btn"
            >
              Видалити
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default BasketComponent;
