import React from 'react';

function TaskCard({ title, description, assignee, dueDate, status }) {
  return (
    <div className="task-card">
      <h3>{title}</h3>
      <p>{description}</p>
      <p><strong>Виконує:</strong> {assignee}</p>
      <p><strong>Термін:</strong> {dueDate}</p>
      <p><strong>Статус:</strong> {status}</p>
    </div>
  );
}

export default TaskCard;
