import React, { useState, useEffect } from 'react';
import { LOCALSTORE_TOTALITEMS } from '../constants';

const GoodsComponent = ({ itemsList }) => {
  const [totalItems, setTotalItems] = useState([]);

  useEffect(() => {
    const savedItems = localStorage.getItem(LOCALSTORE_TOTALITEMS);
    if (savedItems) {
      setTotalItems(JSON.parse(savedItems));
    }
  }, []);

  const addItem = (item) => {
    const newItems = [...totalItems, item];
    setTotalItems(newItems);
    localStorage.setItem(LOCALSTORE_TOTALITEMS, JSON.stringify(newItems));
  };

  return (
    <div className="goods-container">
      <h2>Список товарів</h2>
      {itemsList.map((item) => (
        <div key={item.id} className="goods-item">
          <span>{item.name}</span>
          <button onClick={() => addItem(item)}>Додати до кошика</button>
        </div>
      ))}
    </div>
  );
};

export default GoodsComponent;
