import React from 'react';
import EmployeeCard from './components/EmployeeCard';
import TaskCard from './components/TaskCard';
import './App.css';

function App() {
  // Дані співробітників
  const employees = [
    { id: 1, name: "Іван Петренко", position: "Менеджер", department: "Відділ продажів", email: "ivan@example.com", phone: "+380991234567" },
    { id: 2, name: "Марія Сидоренко", position: "Розробник", department: "IT", email: "maria@example.com", phone: "+380671234567" },
    { id: 3, name: "Олексій Іванов", position: "Бухгалтер", department: "Фінанси", email: "oleksiy@example.com", phone: "+380501234567" }
  ];

  // Дані завдань
  const tasks = [
    { id: 1, title: "Завершити проект", description: "Закінчити розробку нового модуля", assignee: "Іван Петренко", dueDate: "2023-12-31", status: "В процесі" },
    { id: 2, title: "Тестування", description: "Провести тестування системи", assignee: "Марія Сидоренко", dueDate: "2023-11-30", status: "Не розпочато" },
  ];

  return (
    <div className="App">
      <h1>Система управління підприємством</h1>

      {/* Секція співробітників */}
      <section>
        <h2>Співробітники</h2>
        <div className="cards-container">
          {employees.map(employee => (
            <EmployeeCard
              key={employee.id}
              name={employee.name}
              position={employee.position}
              department={employee.department}
              email={employee.email}
              phone={employee.phone}
            />
          ))}
        </div>
      </section>

      {/* Секція завдань */}
      <section>
        <h2>Завдання</h2>
        <div className="cards-container">
          {tasks.map(task => (
            <TaskCard
              key={task.id}
              title={task.title}
              description={task.description}
              assignee={task.assignee}
              dueDate={task.dueDate}
              status={task.status}
            />
          ))}
        </div>
      </section>
    </div>
  );
}

export default App;
