import React from 'react';

function EmployeeCard({ name, position, department, email, phone }) {
  const handleContact = () => {
    alert(`Повідомлення надіслано співробітнику ${name}`);
  };

  return (
    <div className="employee-card">
      <h2>{name}</h2>
      <p><strong>Посада:</strong> {position}</p>
      <p><strong>Відділ:</strong> {department}</p>
      <p><strong>Email:</strong> {email}</p>
      <p><strong>Телефон:</strong> {phone}</p>
      <button onClick={handleContact}>Надіслати повідомлення</button>
    </div>
  );
}

export default EmployeeCard;
